window.addEventListener("DOMContentLoaded",()=>{gsap.timeline().fromTo(".hero__title__main",{x:2e3},{x:0,duration:.8}).fromTo(".hero__title__sub",{x:2e3},{x:0,delay:1,duration:.8}).fromTo(".hero__title__3rd",{x:2e3},{x:0,delay:1,duration:.8}).fromTo(".priceCircle",{scaleX:0,scaleY:0},{scaleX:1,scaleY:1,duration:.8}).fromTo(".hero__eyecatche",{autoAlpha:0},{autoAlpha:1,delay:.5,duration:.5}).fromTo(".annotationLists",{autoAlpha:0},{autoAlpha:1,delay:.5,duration:.1}).add(()=>{console.log("gsap test")}),document.querySelectorAll(".templates__imgBox img").forEach(e=>{e.addEventListener("mouseenter",()=>{gsap.fromTo(e,{scaleX:"100%",scaleY:"100%"},{scaleX:"120%",scaleY:"120%",ease:"power2.out"})}),e.addEventListener("mouseleave",()=>{gsap.fromTo(e,{scaleX:"120%",scaleY:"120%"},{scaleX:"100%",scaleY:"100%",ease:"power2.out"})})})});